import{_ as s,c as e,o as a,a as l}from"./app.28f7b6d5.js";const y=JSON.parse('{"title":"\u5982\u4F55\u64B0\u5199\u9AD8\u6027\u80FD CSS","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u524D\u8A00","slug":"\u524D\u8A00","link":"#\u524D\u8A00","children":[]},{"level":2,"title":"\u907F\u514D\u4F7F\u7528\u901A\u914D\u7B26\u9009\u62E9\u5668","slug":"\u907F\u514D\u4F7F\u7528\u901A\u914D\u7B26\u9009\u62E9\u5668","link":"#\u907F\u514D\u4F7F\u7528\u901A\u914D\u7B26\u9009\u62E9\u5668","children":[]},{"level":2,"title":"\u4ECE\u53F3\u5230\u5DE6\u7684\u5339\u914D\u65B9\u5411","slug":"\u4ECE\u53F3\u5230\u5DE6\u7684\u5339\u914D\u65B9\u5411","link":"#\u4ECE\u53F3\u5230\u5DE6\u7684\u5339\u914D\u65B9\u5411","children":[]},{"level":2,"title":"\u4E0D\u540C\u9009\u62E9\u5668\u7684\u6027\u80FD","slug":"\u4E0D\u540C\u9009\u62E9\u5668\u7684\u6027\u80FD","link":"#\u4E0D\u540C\u9009\u62E9\u5668\u7684\u6027\u80FD","children":[]},{"level":2,"title":"\u4E0D\u8981\u505A\u6CA1\u6709\u5FC5\u8981\u7684\u6807\u8BB0\u9650\u5B9A","slug":"\u4E0D\u8981\u505A\u6CA1\u6709\u5FC5\u8981\u7684\u6807\u8BB0\u9650\u5B9A","link":"#\u4E0D\u8981\u505A\u6CA1\u6709\u5FC5\u8981\u7684\u6807\u8BB0\u9650\u5B9A","children":[]},{"level":2,"title":"\u5C3D\u91CF\u7F29\u77ED\u9009\u62E9\u5668","slug":"\u5C3D\u91CF\u7F29\u77ED\u9009\u62E9\u5668","link":"#\u5C3D\u91CF\u7F29\u77ED\u9009\u62E9\u5668","children":[]},{"level":2,"title":"\u53C2\u8003\u8D44\u6599","slug":"\u53C2\u8003\u8D44\u6599","link":"#\u53C2\u8003\u8D44\u6599","children":[]}],"relativePath":"css/high-performance-css.md"}'),n={name:"css/high-performance-css.md"},o=l(`<h1 id="\u5982\u4F55\u64B0\u5199\u9AD8\u6027\u80FD-css" tabindex="-1">\u5982\u4F55\u64B0\u5199\u9AD8\u6027\u80FD CSS <a class="header-anchor" href="#\u5982\u4F55\u64B0\u5199\u9AD8\u6027\u80FD-css" aria-hidden="true">#</a></h1><h2 id="\u524D\u8A00" tabindex="-1">\u524D\u8A00 <a class="header-anchor" href="#\u524D\u8A00" aria-hidden="true">#</a></h2><p>\u5176\u5B9E\u73B0\u5728\u6765\u8BB2\u9AD8\u6027\u80FD\u4E5F\u6709\u70B9\u6CA1\u5565\u5FC5\u8981\uFF0C\u73B0\u4EE3\u6D4F\u89C8\u5668\u4E0D\u65AD\u5728\u4F18\u5316\u3001\u7EC8\u7AEF\u914D\u7F6E\u4E0D\u65AD\u589E\u5F3A\uFF0C\u5BF9\u4E8E\u589E\u5F3A CSS \u7684\u6027\u80FD\u53EA\u80FD\u9760\u4F18\u5316\u4E00\u4E9B\u8FB9\u8FB9\u89D2\u89D2\u7684\u4E1C\u897F\uFF0C\u4EE5\u53CA\u4E00\u4E9B\u52A0\u8F7D\u65F6\u95F4\u7684\u7F29\u77ED\u3002\u4F46\u662F\u65E0\u8BBA\u600E\u4E48\u8BF4\uFF0C\u53EF\u8BFB\u6027\u548C\u53EF\u7EF4\u62A4\u6027\u662F\u7B2C\u4E00\u4E2A\u9700\u8981\u8003\u8651\u7684\u3002</p><h2 id="\u907F\u514D\u4F7F\u7528\u901A\u914D\u7B26\u9009\u62E9\u5668" tabindex="-1">\u907F\u514D\u4F7F\u7528\u901A\u914D\u7B26\u9009\u62E9\u5668 <a class="header-anchor" href="#\u907F\u514D\u4F7F\u7528\u901A\u914D\u7B26\u9009\u62E9\u5668" aria-hidden="true">#</a></h2><p>\u5C42\u6B21\u7ED3\u6784\u5339\u914D\u662F CSS \u9009\u62E9\u5668\u7684\u4E00\u79CD\uFF0C\u5B83\u53EF\u4EE5\u8BA9\u4F60\u5728\u9009\u62E9\u5668\u4E2D\u4F7F\u7528\u7A7A\u683C\u6765\u5339\u914D\u7236\u5B50\u5143\u7D20\u3002\u4F8B\u5982\uFF1A</p><div class="language-css"><button title="Copy Code" class="copy"></button><span class="lang">css</span><pre class="shiki"><code><span class="line"><span style="color:#FFCB6B;">ul</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">*</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">  </span><span style="color:#B2CCD6;">color</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> red</span><span style="color:#89DDFF;">;</span></span>
<span class="line"><span style="color:#89DDFF;">}</span></span>
<span class="line"></span></code></pre></div><p>\u901A\u914D\u7B26\u9009\u62E9\u5668 <code>(*)</code>\u5B83\u4F1A\u5C1D\u8BD5\u5339\u914D\u6240\u6709\u7684 <code>ul</code> \u5143\u7D20\u7684\u5B50\u5143\u7D20\uFF0C\u800C\u4E0D\u7BA1\u5B83\u4EEC\u662F\u5426\u5728\u6587\u6863\u4E2D\u3002\u8FD9\u79CD\u65B9\u5F0F\u4F1A\u8BA9\u6D4F\u89C8\u5668\u505A\u66F4\u591A\u7684\u5DE5\u4F5C\uFF0C\u6240\u4EE5\u4E0D\u8981\u4F7F\u7528\u8FD9\u79CD\u65B9\u5F0F\u3002</p><h2 id="\u4ECE\u53F3\u5230\u5DE6\u7684\u5339\u914D\u65B9\u5411" tabindex="-1">\u4ECE\u53F3\u5230\u5DE6\u7684\u5339\u914D\u65B9\u5411 <a class="header-anchor" href="#\u4ECE\u53F3\u5230\u5DE6\u7684\u5339\u914D\u65B9\u5411" aria-hidden="true">#</a></h2><p>\u5728 CSS \u4E2D\uFF0C\u9009\u62E9\u5668\u7684\u5339\u914D\u4E0D\u540C\u4E8E\u6211\u4EEC\u9605\u8BFB\u65B9\u5411\uFF0C\u5B83\u662F\u4ECE\u53F3\u5230\u5DE6\u7684\u3002\u4F8B\u5982\uFF1A</p><div class="language-css"><button title="Copy Code" class="copy"></button><span class="lang">css</span><pre class="shiki"><code><span class="line"><span style="color:#FFCB6B;">ul</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&gt;</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">li</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">span</span><span style="color:#89DDFF;">[</span><span style="color:#C792EA;">data-index</span><span style="color:#89DDFF;">*=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">1</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">]</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">  </span><span style="color:#B2CCD6;">color</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> red</span><span style="color:#89DDFF;">;</span></span>
<span class="line"><span style="color:#89DDFF;">}</span></span>
<span class="line"></span></code></pre></div><p>\u8FD9\u9996\u5148\u5C06\u4F1A\u5148\u89E3\u6790 <code>span[data-index*=&quot;1&quot;]</code>\uFF0C\u8FD9\u4E2A\u7B2C\u4E00\u90E8\u5206\u4E5F\u88AB\u79F0\u4E3A \u201Ckey selector\u201D\uFF08\u5173\u952E\u9009\u62E9\u5668\uFF09\uFF0C\u7136\u540E\u518D\u89E3\u6790 <code>li</code>\uFF0C\u518D\u5230 <code>ul</code>\uFF0C\u8FD9\u4E2A\u90E8\u5206\u88AB\u79F0\u4E3A \u201Ccontext selector\u201D\uFF08\u4E0A\u4E0B\u6587\u9009\u62E9\u5668\uFF09\u3002</p><p>\u9009\u62E9\u5668\u9664\u975E\u8981\u589E\u5F3A\u533A\u5206\u5EA6\uFF0C\u4E0D\u7136\u4E0D\u8981\u8FC7\u4E8E\u50CF HTML \u4E00\u6837\u5D4C\u5957\uFF0C\u6BD4\u5982\u8BF4 <code>table tr td</code>. \u5F88\u660E\u663E\uFF0C\u6BCF\u4E00\u4E2A\u8868\u683C\u91CC\u90FD\u6709\u4E00\u4E2A <code>tr</code>\uFF0C\u800C\u6BCF\u4E00\u4E2A <code>tr</code> \u91CC\u90FD\u6709\u4E00\u4E2A <code>td</code>\uFF0C\u6240\u4EE5\u8FD9\u4E2A\u9009\u62E9\u5668\u662F\u591A\u4F59\u7684\uFF0C\u53EF\u4EE5\u7B80\u5316\u4E3A <code>td</code>. \u89E3\u6790\u4E00\u4E2A td \u8FDC\u6BD4\u89E3\u6790\u4E00\u4E2A table tr td \u8981\u5FEB\u3002</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>\u8FD9\u4E48\u505A\u7684\u539F\u56E0\u5176\u5B9E\u60F3\u60F3\u5C31\u77E5\u9053\uFF0C\u5728\u9009\u62E9\u5668\u4ECE\u53F3\u5230\u5DE6\u7684\u5339\u914D\u65F6\uFF0C\u4E00\u65E6\u5339\u914D\u5931\u8D25\u5B83\u5C31\u4F1A\u505C\u6B62\uFF0C\u8FD9\u6837\u4E00\u5B9A\u7A0B\u5EA6\u4E0A\u53EF\u4EE5\u51CF\u5C11\u5DE5\u4F5C\u91CF\u3002</p></div><h2 id="\u4E0D\u540C\u9009\u62E9\u5668\u7684\u6027\u80FD" tabindex="-1">\u4E0D\u540C\u9009\u62E9\u5668\u7684\u6027\u80FD <a class="header-anchor" href="#\u4E0D\u540C\u9009\u62E9\u5668\u7684\u6027\u80FD" aria-hidden="true">#</a></h2><ul><li><code>#id</code>\uFF1AID \u9009\u62E9\u5668\u662F\u6700\u5FEB\u7684\uFF0C\u56E0\u4E3A\u5B83\u662F\u552F\u4E00\u7684\uFF0C\u6240\u4EE5\u6D4F\u89C8\u5668\u4E0D\u9700\u8981\u53BB\u505A\u4EFB\u4F55\u7684\u8BA1\u7B97\u3002</li><li><code>.class</code>\uFF1A\u7C7B\u9009\u62E9\u5668\u662F\u7B2C\u4E8C\u5FEB\u7684\uFF0C\u4F46\u5176\u5B9E\u8DDF ID \u9009\u62E9\u5668\u5DEE\u4E0D\u591A\u4E86\u3002</li><li><code>element</code>\uFF1A\u5143\u7D20\u9009\u62E9\u5668\u662F\u7B2C\u4E09\u5FEB\u7684\uFF0C\u56E0\u4E3A\u5B83\u4E0D\u9700\u8981\u505A\u4EFB\u4F55\u7684\u8BA1\u7B97\u3002</li><li><code>general sibling combinator (~)</code>\uFF1A\u901A\u7528\u5144\u5F1F\u9009\u62E9\u5668\u662F\u7B2C\u56DB\u5FEB\u7684\uFF0C\u56E0\u4E3A\u5B83\u53EA\u9700\u8981\u5339\u914D\u540C\u7EA7\u7684\u5143\u7D20\u3002</li><li><code>child combinator (&gt;)</code>\uFF1A\u5B50\u9009\u62E9\u5668\u662F\u7B2C\u4E94\u5FEB\u7684\uFF0C\u56E0\u4E3A\u5B83\u53EA\u9700\u8981\u5339\u914D\u7236\u5143\u7D20\u7684\u5B50\u5143\u7D20\u3002</li><li><code>descendant combinator ( )</code>\uFF1A\u540E\u4EE3\u9009\u62E9\u5668\u662F\u7B2C\u516D\u5FEB\u7684\uFF0C\u56E0\u4E3A\u5B83\u9700\u8981\u5339\u914D\u6240\u6709\u7684\u540E\u4EE3\u5143\u7D20\u3002</li><li><code>[attribute]</code>\uFF1A\u5C5E\u6027\u9009\u62E9\u5668\u662F\u7B2C\u4E03\u5FEB\u7684\uFF0C\u56E0\u4E3A\u5B83\u9700\u8981\u5339\u914D\u6240\u6709\u7684\u5143\u7D20\u3002</li><li><code>:pseudo-element</code>\uFF1A\u4F2A\u5143\u7D20\u9009\u62E9\u5668\u662F\u6700\u6162\u7684\uFF0C\u56E0\u4E3A\u5B83\u9700\u8981\u5339\u914D\u6240\u6709\u7684\u5143\u7D20\u3002</li></ul><h2 id="\u4E0D\u8981\u505A\u6CA1\u6709\u5FC5\u8981\u7684\u6807\u8BB0\u9650\u5B9A" tabindex="-1">\u4E0D\u8981\u505A\u6CA1\u6709\u5FC5\u8981\u7684\u6807\u8BB0\u9650\u5B9A <a class="header-anchor" href="#\u4E0D\u8981\u505A\u6CA1\u6709\u5FC5\u8981\u7684\u6807\u8BB0\u9650\u5B9A" aria-hidden="true">#</a></h2><p>\u5728 CSS \u4E2D\uFF0CID \u9009\u62E9\u5668\u548C\u7C7B\u9009\u62E9\u5668\u662F\u6700\u5FEB\u7684\u3002</p><div class="language-css"><button title="Copy Code" class="copy"></button><span class="lang">css</span><pre class="shiki"><code><span class="line"><span style="color:#FFCB6B;">ul</span><span style="color:#89DDFF;">#</span><span style="color:#F78C6C;">nav</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">  </span><span style="color:#B2CCD6;">color</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> red</span><span style="color:#89DDFF;">;</span></span>
<span class="line"><span style="color:#89DDFF;">}</span></span>
<span class="line"></span></code></pre></div><p>\u4F46\u662F\u4E0A\u9762\u7684\u4EE3\u7801\u5C31\u5343\u4E07\u522B\u5199\u4E86\uFF0C\u4F7F\u7528 ID \u65F6\u5207\u52FF\u7EE7\u7EED\u8FDB\u884C\u6807\u8BB0\u9650\u5B9A\uFF0C\u5B83\u5DF2\u7ECF\u662F\u6700\u660E\u786E\u7684\u9009\u62E9\u5668\uFF0C\u4EC5\u5728\u4E0D\u53EF\u907F\u514D\u7684\u65F6\u5019\u4F7F\u7528\u7C7B\u7684\u65F6\u5019\u624D\u8FDB\u884C\u6807\u8BB0\u9650\u5B9A\u3002</p><p>\u6BD4\u5982\u8BF4 <code>tag#id</code> \u5E94\u8BE5\u59CB\u7EC8\u662F <code>#id</code>\uFF0C<code>tag.class</code> \u5982\u679C\u53EF\u80FD\u7684\u8BDD\u5E94\u8BE5\u662F <code>.class</code>\uFF0C\u8FD9\u610F\u5473\u7740\u6240\u6709\u60C5\u51B5\u4E0B\u7ED9\u5B9A\u7684\u7C7B\u53EA\u80FD\u7528\u76F8\u540C\u7684\u6807\u7B7E\u627E\u5230\uFF0C\u5F53\u7136\u9664\u975E\u7279\u6B8A\u60C5\u51B5\uFF0C\u4F46\u662F\u5F88\u5C11\u89C1\uFF0C\u6240\u4EE5\u4E00\u822C\u6765\u8BF4\uFF0C\u4E0D\u8981\u3002</p><p>\u5E76\u4E14 ID \u5728\u6240\u6709\u9875\u9762\u90FD\u662F\u552F\u4E00\u7684\uFF0C\u8981\u6C42\u5B83\u5339\u914D\u53E6\u5916\u4E00\u4E2A\u552F\u4E00\u5143\u7D20\u53EF\u80FD\u4F1A\u5BFC\u81F4\u6E32\u67D3\u7684\u5FAA\u73AF\u6D6A\u8D39\u3002</p><h2 id="\u5C3D\u91CF\u7F29\u77ED\u9009\u62E9\u5668" tabindex="-1">\u5C3D\u91CF\u7F29\u77ED\u9009\u62E9\u5668 <a class="header-anchor" href="#\u5C3D\u91CF\u7F29\u77ED\u9009\u62E9\u5668" aria-hidden="true">#</a></h2><p>\u4ECE\u5339\u914D\u65B9\u5411\u6765\u8BB2\uFF0C\u4E5F\u5E94\u8BE5\u5C3D\u53EF\u80FD\u5220\u9664\u53F3\u8FB9 ID \u5DE6\u4FA7\u7684\u9009\u62E9\u5668\u3002\u6BD4\u5982\u8BF4 <code>.class-1 #id-1 nav #id-2 span</code> \u5E94\u8BE5\u662F <code>#id-2 span</code> \u6216\u8005\u662F <code>.class-1 #id-2 span</code>\u3002</p><p>\u9009\u62E9\u5668\u8D8A\u957F\uFF0C\u5B83\u7684\u89E3\u6790\u65F6\u95F4\u5C31\u8D8A\u957F\u3002\u6240\u4EE5\u5C3D\u91CF\u7F29\u77ED\u9009\u62E9\u5668\uFF0C\u6BD4\u5982\u8BF4 <code>#id-1 #id-2 span</code> \u5E94\u8BE5\u662F <code>#id-2 span</code>\u3002\u4E0D\u4EC5\u5982\u6B64\uFF0C\u53EF\u8BFB\u6027\u4E5F\u4F1A\u66F4\u597D\u3002\u5728\u51B3\u5B9A\u662F\u4F7F\u7528\u591A\u4E2A\u5B50\u9009\u62E9\u5668\u8FD8\u662F\u5220\u9664\u4E00\u4E2A\u7236\u9009\u62E9\u5668\u65F6\uFF0C\u5E94\u8BE5\u4F18\u5148\u8003\u8651\u5220\u9664\u7236\u9009\u62E9\u5668\u3002</p><h2 id="\u53C2\u8003\u8D44\u6599" tabindex="-1">\u53C2\u8003\u8D44\u6599 <a class="header-anchor" href="#\u53C2\u8003\u8D44\u6599" aria-hidden="true">#</a></h2><ul><li><a href="https://domhabersack.com/high-performance-css" target="_blank" rel="noreferrer">https://domhabersack.com/high-performance-css</a></li><li><a href="https://shauninman.com/archive/2008/05/05/css_qualified_selectors#comment_3942" target="_blank" rel="noreferrer">https://shauninman.com/archive/2008/05/05/css_qualified_selectors#comment_3942</a></li><li><a href="https://css-tricks.com/efficiently-rendering-css/" target="_blank" rel="noreferrer">https://css-tricks.com/efficiently-rendering-css/</a></li><li><a href="https://developer.mozilla.org/en-US/docs/MDN/Writing_guidelines/Writing_style_guide/Code_style_guide/CSS" target="_blank" rel="noreferrer">https://developer.mozilla.org/en-US/docs/MDN/Writing_guidelines/Writing_style_guide/Code_style_guide/CSS</a></li><li><a href="https://blog.zwying.com/archives/70.html" target="_blank" rel="noreferrer">https://blog.zwying.com/archives/70.html</a></li></ul>`,26),c=[o];function t(r,p,i,d,h,D){return a(),e("div",null,c)}const u=s(n,[["render",t]]);export{y as __pageData,u as default};
